import React from 'react'
import "./ProfileDesc.css"

//今はどちらの構成も見るためにstateを渡しているが最終的にはユーザの種別を渡して判断するようにする
//文字数に制限を設ける
// 一言の文字数は300字までにする
export default function ProfileDesc({user}) {

  const [ city, subject, method] = user;

  const checkMethod = () => {
    if (user.method) {
      return (
        <span>オンラインも可能</span>
      )
    } else {
      return (
        <span>対面</span>
      )
    }
  }

  const teacherdesc = () => {
    return (
      <>
        <div className="type">
          お住まい（市まで）:<br className='hidden' />
          {city}
        </div>
        <div className="type">
          対応できる教科:
          {subject}
        </div>
        <div className="type">
          授業形態:<br/>     
          {chec}
        </div>
      </>
    )
  }



  return (
    <div className="profiledesc">
      <div className="descWrapper">
        {teacherdesc}
      </div>
    </div>
  )
}
