import axios from 'axios';
import React, { useContext, useRef, useState } from 'react'
import { useNavigate } from 'react-router-dom';
import { studentUpdate, teacherUpdate } from '../../actionCalls';
import { AuthContext } from '../../state/AuthContext';
import "./Settings.css"


//反映させるにはログインしなおす

export default function Settings() {
  const { user, dispatch } = useContext(AuthContext)

  const navigation = useNavigate();


  const [method, setMethod] = useState(user.method);
  const [username, setuserName] = useState(user.username);
  const [email, setEmail] = useState(user.email);
  const [password, setPassword] = useState(user.password);
  const [city, setCity] = useState(user.city);

  const [subject, setSubject] = useState(user.subject);

  // プロフィール画像
  const [file, setFile] = useState(user.profilePicture);

  const handleSubmit = async (e) => {
    e.preventDefault();

    if(file) {
      //キーとバリューを合わせてデータとして持っておく
      const data = new FormData();
      //同じ画像がアップロードされることも考えられるので現在時刻を付与する
      const fileName = Date.now() + file.name;
      data.append("name", fileName);
      data.append("file", file);
      setFile(fileName);

      try {
        //画像アップロード
        await axios.post("/upload", data);
        // setFile(fileName);
      } catch(err) {
        console.log(err);
      }
    }

    try {
      const newUser = {
        _id: user._id,
        profilePicture: file,
        username: username,
        email: email,
        password: password,
        city: city,
        subject: subject,
        method: method,
        type: user.type
      }
      if(user.type) {
        console.log(newUser._id);
        await axios.put(`users/teacher/${newUser._id}`, newUser);

        localStorage.setItem("user", JSON.stringify(newUser));
      } else {
        await axios.put(`users/student/${user._id}`, newUser);
        // studentUpdate(newUser,dispatch);
      }

      navigation("/home");
    } catch (err) {
      console.log(err);
    }

  }





  const SettingTeacher = () => {
    return (
      <div className="settings">
        <div className="settingsWrapper">
          <h1 className="settingTop">
            設定
          </h1>
          <form className="settingBottom" onSubmit={(e) => handleSubmit(e)}>
            <div className="settingItems">
              <div className='settingItem' >
                <p>名前</p>
                <input
                  type="text"
                  className="settingInput"
                  required
                  onChange={(e) => setuserName(e.target.value)}
                  value={username}
                />
              </div>   
              <div className='settingItem' >
                <p>メールアドレス</p>
                <input
                  type="email"
                  className="settingInput"
                  required
                  onChange={(e) => setEmail(e.target.value)}
                  value={email}
                />
              </div>
              <div className='settingItem' >
                <p>パスワード</p>
                <input
                  type="password"
                  className="settingInput" 
                  required
                  minLength="6"
