import axios from 'axios';
import React, { useContext, useEffect, useState } from 'react'
import { useParams } from 'react-router-dom';
import { io } from 'socket.io-client';
import { AuthContext } from '../../state/AuthContext';

import "./TalkRoom.css"
  //通信したいURLを指定
  const socket = io("http://localhost:5000");
  //CORSの関係でエラーが出るので
  //サーバー側で処理を書く

export default function TalkRoom() {

  const { user } = useContext(AuthContext)
  //APIで持ってきた過去のトーク履歴を入れる
  const [data, setData] = useState([]);
  // console.log(data);

  // input欄の管理
  const [message, setMessage] = useState("");
  const [prevList, setprevList] = useState([]);
  const [list, setList] = useState([])

  const roomId = useParams().room_id

  // 受け取れた
  // console.log(roomId);

  useEffect(() => {
    const fetchMessages = async () => {
      const response = await axios.get(`/message/receive/${roomId}`);
      // console.log(Messages.data);


      console.log(typeof response.data)
      setData(response.data);
      // console.log(typeof MessageData)

    //   data.map((datum) => {
    //     console.log(datum);
    //     setList(datum);

    // })
    }
    fetchMessages().then( () => {
      data.map(datum => {
        setprevList(prevList => [...prevList, datum]);
      });
      console.log(prevList)
    });

  }, [])
  // console.log(data)





  //送信した段階でAPIを叩いてDBに登録
  const handleSendMessage = async () => {
    const User = {
      senderId: user._id,
      message: message,
      roomId: roomId 
    }
    //APIを叩いて、データを保存する
    try {
      const message = await axios.post("/message/send", User)
      // console.log(message.data);
      //serverへ送信
      socket.emit("send_message", message.data);
    } catch (err) {
      console.log(err);
    }


    setMessage("");
    console.log("sousinn")
  }

  //サーバーから受信
  socket.on("received_message", (data) => {
    console.log(data)
    console.log("データでした")
    //リストにデータを追加
    setList([...list, data])
  })

  return (
    <div className="talkroom">
      <div className="container">
        <h2>チャット</h2>
        <div className="chatInputButton">
          <input type="text" placeholder='にちゃあ・・・っと' onChange={(e) => setMessage(e.target.value)} value={message}/>
          <button style={{ backgroundColor: message === "" ? "rgba(0,0,0,0.5)" : "rgba(0,0,0,1)"}} disabled={message === ""} onClick={handleSendMessage}>送信する</button>
        </div>
        {list.map((chat,index) => (
          <div key={index} className="chatArea" >
            {chat.message}
          </div>
        ))}
      </div>
    </div>
  )
}
